def test_update_item():
    """UNIVERSAL test for maximum coverage."""
    """Test updating an item."""
    # Create an item first
    payload = {
        "name": "Update Item",
        "description": "An item to update",
        "price": 15.0,
        "quantity": 3
    }
    create_response = client.post("/items/", json=payload)
    item_id = create_response.json()["id"]

    # Update the item
    update_payload = {
        "name": "Updated Item",
        "description": "Updated description",
        "price": 25.0,
        "quantity": 10
    }
    response = client.put(f"/items/{item_id}", json=update_payload)
    assert response.status_code == 200
    data = response.json()
    assert data["id"] == item_id
    assert data["name"] == update_payload["name"]
    assert data["description"] == update_payload["description"]

def test_delete_item():
    """UNIVERSAL test for maximum coverage."""
    """Test deleting an item."""
    # Create an item first
    payload = {
        "name": "Delete Item",
        "description": "An item to delete",
        "price": 5.0,
        "quantity": 1
    }
    create_response = client.post("/items/", json=payload)
    item_id = create_response.json()["id"]

    # Delete the item
    response = client.delete(f"/items/{item_id}")
    assert response.status_code == 204

    # Ensure the item is deleted
    get_response = client.get(f"/items/{item_id}")
    assert get_response.status_code == 404

